const env = require('../src/config/env');
const schemas = require('./components/schemas');

module.exports = {
	openapi: '3.0.0',
	info: {
		title: 'API recomendación gasolineras',
		version: '0.1.0',
		description: 'Documentación de la API para administración de empresas, conductores y vehículos'
	},
	servers: [
		{ url: env.appBaseUrl }
	],
	tags: [
		{ name: 'Auth' },
		{ name: 'Companies' },
		{ name: 'Drivers' },
		{ name: 'Receipts' },
		{ name: 'Stations' },
		{ name: 'Vehicles' },
	],
	paths: {
		...require('./paths/auth'),
		...require('./paths/drivers'),
		...require('./paths/companies'),
		...require('./paths/receipts'),
		...require('./paths/stations'),
		...require('./paths/vehicles'),
	},
	components: {
		schemas,
		securitySchemes: {
			bearerAuth: {
				type: 'http',
				scheme: 'bearer',
				bearerFormat: 'JWT'
			}
		}
	}
}

const credentials = {
	email: { type: 'string' },
	password: { type: 'string' },
};

const company = {
	name: { type: 'string' },
	CIF: { type: 'string' },
	postal_code: { type: 'integer' },
	address: { type: 'string' },
	state: { type: 'string' },
	payment_entity: { type: 'string' }
};

const vehicle = {
	brand: { type: 'string' },
	model: { type: 'string' },
	license_plate: { type: 'string' },
	type: { type: 'string', enum: ['gas', 'electric'] },
	registration_date: { type: 'string', format: 'date-time' }
};

const driver = {
	first_name: { type: 'string' },
	last_name: { type: 'string' }
};

const receipt = {
	quantity: { type: 'number' },
	price: { type: 'integer' },
	mileage: { type: 'integer' },
	fuel_type: { type: 'string', enum: ['95','98','glp','diesel','electric'] }
};

const evstation = {
	id: { type: 'integer' },
	operator: { type: 'string' },
	address: { type: 'string' },
	postal_code: { type: 'integer' },
	state: { type: 'string' },
	city: { type: 'string' },
	location: { type: 'string' },
	latitude: { type: 'number' },
	longitude: { type: 'number' },
	max_power: { type: 'integer' },
	distance: { type: 'number' }
};

module.exports = {
	Vehicle: {
		type: 'object',
		properties: {
			id: { type: 'integer' },
			company_id: { type: 'integer' },
			...vehicle,
			in_use_by: { type: 'integer', nullable: true }
		}
	},
	VehicleInput: {
		type: 'object',
		required: Object.keys(vehicle),
		properties: {
			...vehicle
		}
	},
	VehicleUpdate: {
		type: 'object',
		properties: {
			...vehicle
		}
	},
	Login: {
		type: 'object',
		required: ['email', 'password'],
		properties: {
			...credentials
		}
	},
	LoginResponse: {
		type: 'object',
		properties: {
			requires2FA: { 
				type: 'boolean',
				required: false
			},
			role: { type: 'string', enum: ['admin','company','driver']},
			user: {
				type: 'object',
				nullable: true,
				oneOf: [
					{ $ref: '#/components/schemas/Driver'},
					{ $ref: '#/components/schemas/Company'},
				]
			},
		}
	},
	CompanyInput: {
		type: 'object',
		required: Object.keys({...company,...credentials}),
		properties: {
			...credentials,
			...company
		}
	},
	Company: {
		type: 'object',
		properties: {
			id: { type: 'integer' },
			...company,
			subscription_active: { type: 'boolean' }
		}
	},
	DriverInput: {
		type: 'object',
		required: Object.keys({...driver,...credentials}),
		properties: {
			...credentials,
			...driver
		}
	},
	Driver: {
		type: 'object',
		properties: {
			id: { type: 'integer' },
			company_id: { type: 'integer' },
			...driver,
			Vehicle: {
				type: 'object',
				nullable: true,
				$ref: '#/components/schemas/Vehicle'
			}
		}
	},
	Enable2FA: {
		type: 'object',
		required: ['code'],
		properties: {
			code: { type: 'string' },
		}
	},
	Enable2FAResponse: {
		type: 'object',
		properties: {
			secret: { type: 'string' },
			qrDataURL: { type: 'string' }
		}
	},
	ReceiptInput: {
		type: 'object',
		required: Object.keys(receipt),
		properties: {
			...receipt
		}
	},
	EVStation: {
		type: 'object',
		properties: {
			...evstation
		}
	},
	Error: {
		type: 'object',
		properties: {
			error: { type: 'string' }
		}
	}
}

const security = require('../security');

module.exports = {
	'/auth/login': {
		post: {
			tags: ['Auth'],
			summary: 'Login',
			description: 'Login endpoint. The response object contains a token and whether or not a 2FA code is needed. If the 2FA is not needed, it also returns the user object with info about the company or the driver',
			requestBody: {
				required: true,
				content: {
					'application/json': {
						schema: { $ref: '#/components/schemas/Login' }
					}
				}
			},
			responses: {
				200: {
					description: 'Login successful',
					content: {
						'application/json': { 
							schema: { $ref: '#/components/schemas/LoginResponse' }
						}
					}
				},
				401: { description: 'Unauthorized: Email not verified' },
				404: { description: 'Wrong credentials' },
				429: { description: 'Too many requests' }
			}
		}
	},
	'/auth/2fa': {
		post: {
			tags: ['Auth'],
			summary: 'Send 2FA code to finish login',
			security: security.jwt,
			requestBody: {
				required: true,
				content: {
					'application/json': {
						schema: { $ref: '#/components/schemas/Enable2FA' }
					}
				}
			},
			responses: {
				200: {
					description: '2FA enabled',
					content: {
						'application/json': { 
							schema: { $ref: '#/components/schemas/LoginResponse' }
						}
					}
				},
				401: { description: 'Invalid token, expired or wrong code' },
				429: { description: 'Too many requests' }
			}
		},
	},
	'/auth/2fa/enable': {
		get: {
			tags: ['Auth'],
			summary: 'Get TOTP secret to enable 2FA',
			security: security.jwt,
			responses: {
				200: {
					description: 'Registered vehicle',
					content: {
						'application/json': { 
							schema: { $ref: '#/components/schemas/Enable2FAResponse' }
						}
					}
				},
				401: { description: 'Unauthorized' },
				409: { description: 'Already enabled' },
			}
		},
		post: {
			tags: ['Auth'],
			summary: 'Send 2FA code to enable it',
			security: security.jwt,
			requestBody: {
				required: true,
				content: {
					'application/json': {
						schema: { $ref: '#/components/schemas/Enable2FA' }
					}
				}
			},
			responses: {
				204: { description: '2FA enabled' },
				401: { description: 'Invalid token, expired or wrong code' },
				409: { description: '2FA already enabled' }
			}
		},
	},
	'/auth/verify': {
		get: {
			tags: ['Auth'],
			summary: 'Verify email',
			parameters: [
				{ name: 'token', in: 'query', required: true, description: 'Verification token', schema: { type: 'string' }}
			],
			responses: {
				204: {
					description: 'Email successfully verified',
					content: {
						'application/json': { 
							schema: { $ref: '#/components/schemas/Vehicle' }
						}
					}
				},
				400: { description: 'Missing token' },
				401: { description: 'Unauthorized' },
				429: { description: 'Too many requests' }
			}
		},
		post: {
			tags: ['Auth'],
			summary: 'Resend verification email',
			requestBody: {
				required: true,
				content: {
					'application/json': {
						schema: {
							type: 'object',
							required: ['email'],
							properties: {
								email: { type: 'string' }
							}
						}
					}
				}
			},
			responses: {
				204: { description: 'Email sent or not found in DB' },
				429: { description: 'Too many requests' }
			}
		}
	}
}

