const { User, Company, Driver, Vehicle } = require('../models');
const { verifyLogin } = require('../utils/jwt');

module.exports = {
	authenticate: async (req,res,next) => {
		try {
			const token = req.cookies.token;
			const { payload, err } = verifyLogin(token);
			if(err) return res.status(401).send({ error: err });
			// TODO validate tokens in the DB?
			req.user = await User.findByPk(payload.sub, {
				include: [
					Company,
					{ model:Driver, include:[Vehicle]}
				]
			});
			next();
		} catch (error) {
			console.log(error);
			return res.status(500).send({ error: "Internal Server Error" });
		}
	},
	hasRole: (...roles) => async (req,res,next) => {
		if(!req.user) return res.status(500).send({ error: "Internal Server Error: Missing authentication."});
		if(!roles.includes(req.user.role)) return res.status(403).send({ error: `Access Forbidden: Allowed roles: ${roles.join(', ')}`});
		next();
	},
	limitCompanyScope: (req) => {
		const role = req.user.role;
		if(role==="admin") return null;
		if(role==="company") return req.user.company_id;
		if(role==="driver") return req.user.Driver.company_id;
		throw new Error('Role not implemented');
	}
}

module.exports = (sequelize, DataTypes) => {
	const Company = sequelize.define('Company', {
		id: {
			type: DataTypes.INTEGER.UNSIGNED,
			autoIncrement: true,
			primaryKey: true
		},
		name: {
			type: DataTypes.STRING(60),
			allowNull: false
		},
		postal_code: {
			type: DataTypes.INTEGER.UNSIGNED,
			allowNull: false
		},
		address: {
			type: DataTypes.STRING(150),
			allowNull: false
		},
		state: {
			type: DataTypes.STRING(40),
			allowNull: false
		},
		CIF: {
			type: DataTypes.CHAR(9),
			allowNull: false,
			unique: true,
			validate: {
				validCIF(cif) {
					if(!cif || typeof cif != "string" || cif.length != 9) return false;
					if(!/^[A-HJNP-SU-W]\d{7}[0-9A-J]$/.test(cif)) return false;
					if((cif.substring(1,3) === "00" || /[NP-SW]/.test(cif[0])) && !/[A-J]/.test(cif[8])) return false;
					if(/[ABEH]/.test(cif[0]) && !/\d/.test(cif[8])) return false;

					let control = cif[8];
					if(/[A-J]/.test(control)) control = (cif[8].charCodeAt(0)-54)%10;
					const sumEven = (+cif[2]) + (+cif[4]) + (+cif[6]);
					let sumOdd = 0;
					for(let i = 1; i <= 7; i+=2) {
						const prod = 2*(+cif[i]);
						sumOdd += prod < 10 ? prod:prod-9;
					}
					const res = (10-(sumEven + sumOdd)%10)%10;
					return res == control;
				}
			}
		},
