import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import authService from './authService';

const initialState = {
	user: null,
	role: null,
	requires2FA: false,
	target: {
		latitude: 0,
		longitude: 0,
	},
};

export const getUserInfo = createAsyncThunk('auth/info', async (_, { rejectWithValue }) => {
	try {
		console.log('wrfsery');
		const userData = await authService.userInfo();
		return userData;
	} catch (err) {
		return rejectWithValue(err.response?.data?.message || 'Error de red o del servidor');
	}
});

export const loginUser = createAsyncThunk(
	'auth/login',
	async (credentials, { rejectWithValue }) => {
		try {
			const userData = await authService.userLogin(credentials);
			return { ...userData, location: DataTransfer.location };
		} catch (err) {
			return rejectWithValue(err.response?.data?.message || 'Error de red o del servidor');
		}
	}
);

export const logoutUser = createAsyncThunk('auth/logout', async (_, { rejectWithValue }) => {
	try {
		return await authService.userLogout();
	} catch (err) {
		return rejectWithValue(err.response?.data?.message || 'Error de red o del servidor');
	}
});

export const registerDriver = createAsyncThunk('auth/registerDriver', async user => {
	console.log('desde store', user);
});

export const verify2FA = createAsyncThunk('/auth/2fa', async (code, { rejectWithValue }) => {
	try {
		const response = await authService.verify2FA(code);
		return response;
	} catch (err) {
		return rejectWithValue('Código 2FA incorrecto o expirado', err);
	}
});

export const authSlice = createSlice({
	name: 'auth',
	initialState,
	reducers: {
		resetAuthState: () => initialState,
	},
	extraReducers: builder => {
		builder.addCase(loginUser.fulfilled, (state, action) => {
			state.target = action.payload.location;
			state.requires2FA = action.payload.requires2FA;
			state.role = action.payload.role ?? null;
			state.user = action.payload.user ?? null;
		});
		builder.addCase(logoutUser.fulfilled, state => {
			state.target = null;
			state.requires2FA = null;
			state.role = null;
			state.user = null;
		});
		builder.addCase(verify2FA.fulfilled, (state, action) => {
			state.user = action.payload.user;
			state.role = action.payload.role;
			state.requires2FA = false;
		});
		builder.addCase(getUserInfo.fulfilled, (state, action) => {
			state.role = action.payload.role;
			state.user = action.payload.user;
		});
	},
});
export const { resetAuthState } = authSlice.actions;
export default authSlice.reducer;

import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import oilService from './oilService';

const initialState = {
	provincias: [],
	municipios: [],
	gastations: [],
	gastationDetails: null,
	gastationNextMe: [],
	isLoading: false,
	isError: false,
	message: '',
};

export const getProvincias = createAsyncThunk('oil/getProvincias', async thunkAPI => {
	try {
		return await oilService.getProvincias();
	} catch (error) {
		const message = error.response?.data?.message || error.message || error.toString();
		return thunkAPI.rejectWithValue(message);
	}
});

export const getMunicipios = createAsyncThunk(
	'oil/getMunicipios',
	async (idProvincia, thunkAPI) => {
		try {
			return await oilService.getMunicipios(idProvincia);
		} catch (error) {
			console.error(error);
			const message = error.response?.data?.message || error.message || error.toString();
			return thunkAPI.rejectWithValue(message);
		}
	}
);

export const getGastations = createAsyncThunk(
	'oil/getGastations',
	async (idMunicipio, thunkAPI) => {
		try {
			return await oilService.getGastations(idMunicipio);
		} catch (error) {
			const message = error.response?.data?.message || error.message || error.toString();
			return thunkAPI.rejectWithValue(message);
		}
	}
);

export const getGastationDetails = createAsyncThunk(
	'oil/getGastationDetails',
	async (idEstacion, thunkAPI) => {
		try {
			return await oilService.getGastationDetails(idEstacion);
		} catch (error) {
			const message = error.response?.data?.message || error.message || error.toString();
			return thunkAPI.rejectWithValue(message);
		}
	}
);

export const getGastationNextMe = createAsyncThunk(
	'oil/getGastationNextMe',
	async ({ latitud, longitud, radio }, thunkAPI) => {
		try {
			return await oilService.getGastationNextMe(latitud, longitud, radio);
		} catch (error) {
			const message = error.response?.data?.message || error.message || error.toString();
			return thunkAPI.rejectWithValue(message);
		}
	}
);

export const oilSlice = createSlice({
	name: 'oil',
	initialState,
	reducers: {
		reset: state => {
			state.isLoading = false;
			state.isError = false;
			state.message = '';
		},
	},
	extraReducers: builder => {
		builder
			.addCase(getProvincias.pending, state => {
				state.isLoading = true;
				state.isError = false;
			})
			.addCase(getProvincias.fulfilled, (state, action) => {
				state.isLoading = false;
				state.provincias = action.payload;
			})
			.addCase(getProvincias.rejected, (state, action) => {
				state.isLoading = false;
				state.isError = true;
				state.message = action.payload;
			})
			.addCase(getMunicipios.pending, state => {
				state.isLoading = true;
				state.isError = false;
				state.municipios = [];
			})
			.addCase(getMunicipios.fulfilled, (state, action) => {
				state.isLoading = false;
				state.municipios = action.payload;
			})
			.addCase(getMunicipios.rejected, (state, action) => {
				state.isLoading = false;
				state.isError = true;
				state.message = action.payload;
				state.municipios = [];
			})
			.addCase(getGastations.pending, state => {
				state.isLoading = true;
				state.isError = false;
			})
			.addCase(getGastations.fulfilled, (state, action) => {
				state.isLoading = false;
				state.gastations = action.payload;
			})
			.addCase(getGastations.rejected, (state, action) => {
				state.isLoading = false;
				state.isError = true;
				state.message = action.payload;
			})
			.addCase(getGastationDetails.pending, state => {
				state.isLoading = true;
				state.isError = false;
			})
			.addCase(getGastationDetails.fulfilled, (state, action) => {
				state.isLoading = false;
				state.gastationDetails = action.payload;
			})
			.addCase(getGastationDetails.rejected, (state, action) => {
				state.isLoading = false;
				state.isError = true;
				state.message = action.payload;
			})
			.addCase(getGastationNextMe.pending, state => {
				state.isLoading = true;
				state.isError = false;
			})
			.addCase(getGastationNextMe.fulfilled, (state, action) => {
				state.isLoading = false;
				state.gastationNextMe = action.payload;
			})
			.addCase(getGastationNextMe.rejected, (state, action) => {
				state.isLoading = false;
				state.isError = true;
				state.message = action.payload;
				state.gastationNextMe = [];
			});
	},
});

export const { reset } = oilSlice.actions;
export default oilSlice.reducer;

import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import vehicleService from './vehicleService';

const initialState = {
	vehicles: [],
	selectedVehicle: null,
	status: 'idle',
	error: null,
};

export const createVehicle = createAsyncThunk(
	'vehicles/createVehicle',
	async (vehicleData, { rejectWithValue }) => {
		try {
			return await vehicleService.create(vehicleData);
		} catch (error) {
			return rejectWithValue(error.response?.data?.error || 'Error al crear el vehículo');
		}
	}
);

export const fetchVehicles = createAsyncThunk(
	'vehicles/fetchVehicles',
	async (_, { rejectWithValue }) => {
		try {
			return await vehicleService.getAll();
		} catch (error) {
			const message = error.response?.data?.error || 'Error al cargar los vehículos';
			return rejectWithValue(message);
		}
	}
);

export const fetchVehicleById = createAsyncThunk(
	'vehicles/fetchVehicleById',
	async (id, { rejectWithValue }) => {
		try {
			return await vehicleService.getById(id);
		} catch (error) {
			const message = error.response?.data?.error || 'Error al cargar el vehículo';
			return rejectWithValue(message);
		}
	}
);

export const updateVehicle = createAsyncThunk(
	'vehicles/updateVehicle',
	async (vehicleData, { rejectWithValue }) => {
		try {
			const { id, ...fields } = vehicleData;
			return await vehicleService.update(id, fields);
		} catch (error) {
			const message = error.response?.data?.error || 'Error al actualizar el vehículo';
			return rejectWithValue(message);
		}
	}
);

export const deleteVehicle = createAsyncThunk(
	'vehicles/deleteVehicle',
	async (id, { rejectWithValue }) => {
		try {
			await vehicleService.remove(id);
			return id; // En caso de éxito, se devuelve el ID
		} catch (error) {
			const message = error.response?.data?.error || 'Error al eliminar el vehículo';
			return rejectWithValue(message);
		}
	}
);

export const assignVehicle = createAsyncThunk(
	'vehicles/assignVehicle',
	async ({ vehicleId, driverId }, { rejectWithValue }) => {
		try {
			return await vehicleService.assign(vehicleId, driverId);
		} catch (error) {
			const message = error.response?.data?.error || 'Error al asignar el vehículo';
			return rejectWithValue(message);
		}
	}
);

export const unassignVehicle = createAsyncThunk(
	'vehicles/unassignVehicle',
	async (vehicleId, { rejectWithValue }) => {
		try {
			await vehicleService.unassign(vehicleId);
			return vehicleId;
		} catch (error) {
			const message = error.response?.data?.error || 'Error al desasignar el vehículo';
			return rejectWithValue(message);
		}
	}
);

const vehicleSlice = createSlice({
	name: 'vehicles',
	initialState,
	reducers: {},
	extraReducers: builder => {
		builder
			// createVehicle
			.addCase(createVehicle.pending, state => {
				state.status = 'loading';
				state.error = null;
			})
			.addCase(createVehicle.fulfilled, (state, action) => {
				state.status = 'succeeded';
				state.vehicles.push(action.payload);
			})
			.addCase(createVehicle.rejected, (state, action) => {
				state.status = 'failed';
				state.error = action.payload;
			})
			// fetchVehicles
			.addCase(fetchVehicles.pending, state => {
				state.status = 'loading';
			})
			.addCase(fetchVehicles.fulfilled, (state, action) => {
				state.status = 'succeeded';
				state.vehicles = action.payload;
			})
			.addCase(fetchVehicles.rejected, (state, action) => {
				state.status = 'failed';
				state.error = action.error.message;
			})
			// fetchVehicleById
			.addCase(fetchVehicleById.pending, state => {
				state.status = 'loading';
			})
			.addCase(fetchVehicleById.fulfilled, (state, action) => {
				state.status = 'succeeded';
				state.selectedVehicle = action.payload;
			})
			// updateVehicle
			.addCase(updateVehicle.fulfilled, (state, action) => {
				state.status = 'succeeded';
				state.selectedVehicle = action.payload;

				const index = state.vehicles.findIndex(v => v.id === action.payload.id);
				if (index !== -1) {
					state.vehicles[index] = action.payload;
				}
			})
			// deleteVehicle
			.addCase(deleteVehicle.fulfilled, (state, action) => {
				state.status = 'succeeded';

				state.vehicles = state.vehicles.filter(v => v.id !== action.payload);
				state.selectedVehicle = null;
			})
			//assignVehicle;
			.addCase(assignVehicle.fulfilled, (state, action) => {
				state.status = 'succeeded';
				const updatedVehicle = action.payload;
				const index = state.vehicles.findIndex(v => v.id === updatedVehicle.id);
				if (index !== -1) {
					state.vehicles[index] = updatedVehicle;
				}
				if (state.selectedVehicle && state.selectedVehicle.id === updatedVehicle.id) {
					state.selectedVehicle = updatedVehicle;
				}
			})
			//unassignVehicle;
			.addCase(unassignVehicle.fulfilled, (state, action) => {
				state.status = 'succeeded';
				const vehicleId = action.payload;
				const index = state.vehicles.findIndex(v => v.id === vehicleId);
				if (index !== -1) {
					state.vehicles[index].in_use_by = null;
				}
				if (state.selectedVehicle && state.selectedVehicle.id === vehicleId) {
					state.selectedVehicle.in_use_by = null;
				}
			});
	},
});

export default vehicleSlice.reducer;
